  const [typeSeed, setTypeSeed] = useState("")
  const [nonValid, setNonValid] = useState(false)
  const navigate = useNavigate()

  const seedAdjust = (e) => {
    setTypeSeed(e.target.value)
    setNonValid(false)
  }

